import React, { Component } from 'react'
import PropTypes from 'prop-types'
import { removeTodoItemAction, toggleTodoItemAction } from '../actions/todos'
import { connect } from 'react-redux'
import './Todoitem.css'

// 待办事项列表项
class TodoItem extends Component {
  static propTypes = {
    id: PropTypes.number,
    title: PropTypes.string,
    completed: PropTypes.bool
  }
  static defaultProps = {
    id: 0,
    title: '',
    completed: false
  }

  render() {
    const { id, title, completed } = this.props
    return (
      <ul>
      <li>
        <span style={{color: completed ? '#f00' : '#0f0'}}>
        {title}
        </span>
        <>
          <button onClick={() => { this.props.toggle(id)}}>标记为{completed?'未':'已'}完成</button>

          <button style={{ float:'right'}}>编辑</button>
          
          <button onClick={() => { this.props.remove(id)}} style={{ float:'right'}}>删除</button>

        </>
      </li>
      </ul>
    )
  }
}

const mapDispatchToProps = dispatch => {
  return {
    toggle: id => dispatch(toggleTodoItemAction(id)),
    remove: id => dispatch(removeTodoItemAction(id))
  }
}
export default connect(null, mapDispatchToProps)(TodoItem)
