import React, { Component } from 'react'
import ReactDOM from 'react-dom'
import PropTypes from 'prop-types'
import { removeTodoItemAction, toggleTodoItemAction, changedTodoItemAction } from '../actions/todos'
import { connect } from 'react-redux'
import { Card, Input } from 'antd';
import 'antd/dist/antd.css';
import './Todoitem.css'

const { TextArea } = Input;
// 待办事项列表项
class TodoItem extends Component {
  static propTypes = {
    id: PropTypes.number,
    title: PropTypes.string,
    completed: PropTypes.bool
  }
  static defaultProps = {
    id: 0,
    title: '',
    completed: false
  }

  render() {
    const { id, title, completed } = this.props
    return (
      <ul>
      <li>
        <>
          <TextArea rows={4} value={title} />
          {/* <Card className="Card" title="待办事项" extra={<a href="#">More</a>} style={{ width: 300,color: completed ? '#f00' : '#0f0' }}>
          {title}
          </Card> */}
        </>
        <>
          <button onClick={() => { this.props.toggle(id)}}>标记为{completed?'未':'已'}完成</button>

          <button onClick={() => { this.props.changed(title)}} style={{ float:'right'}}>编辑</button>
          
          <button onClick={() => { this.props.remove(id)}} style={{ float:'right'}}>删除</button>

        </>
      </li>
      </ul>
    )
  }
}

const mapDispatchToProps = dispatch => {
  return {
    toggle: id => dispatch(toggleTodoItemAction(id)),
    remove: id => dispatch(removeTodoItemAction(id)),
    changed: title => dispatch(changedTodoItemAction(title))
  }
}
export default connect(null, mapDispatchToProps)(TodoItem)
